package name.neuhalfen.projects.crypto.bouncycastle.openpgp.keys.generation.type.length;

import java.math.BigInteger;

/**
 * The following primes are taken from RFC-3526.
 *
 * @see <a href="https://www.ietf.org/rfc/rfc3526.txt">
 *     RFC-3526: More Modular Exponential (MODP) Diffie-Hellman groups for Internet Key Exchange
 *     (IKE)</a>
 */
@SuppressWarnings({"PMD.ShortVariable"})
public enum ElGamalLength implements KeyLength {
// FIXME: here? http://www.bouncycastle.org/wiki/display/JA1/Elliptic+Curve+Key+Pair+Generation+and+Key+Factories
//
  /**
   * prime: 2^1536 - 2^1472 - 1 + 2^64 * { [2^1406 pi] + 741804 }.
   * generator: 2
   */
  ElGamal_1536_BIT(1536,
      "",
      "2"),

  /**
   * prime: 2^2048 - 2^1984 - 1 + 2^64 * { [2^1918 pi] + 124476 }.
   * generator: 2
   */
  ElGamal_2048_BIT(2048,
      "",
      "2"),

  /**
   * prime: 2^3072 - 2^3008 - 1 + 2^64 * { [2^2942 pi] + 1690314 }.
   * generator: 2
   */
  ElGamal_3072_BIT(3072,
      "",
      "2"),

  /**
   * prime: 2^4096 - 2^4032 - 1 + 2^64 * { [2^3966 pi] + 240904 }.
   * generator: 2
   */
  ElGamal_4096_BIT(4096,
      "",
      "2"),

  /**
   * prime: 2^6144 - 2^6080 - 1 + 2^64 * { [2^6014 pi] + 929484 }.
   * generator: 2
   */
  ElGamal_6144_BIT(6144,
      "",
      "2"),

  /**
   * prime: 2^8192 - 2^8128 - 1 + 2^64 * { [2^8062 pi] + 4743158 }.
   * generator: 2
   */
  ElGamal_8192_BIT(8192,
      "",
      "2");

  private final int length;
  private final BigInteger p;
  private final BigInteger g;

  ElGamalLength(int length, String p, String g) {
    this.length = length;
    this.p = new BigInteger(p, 16);
    this.g = new BigInteger(g, 16);
  }

  @Override
  public int getLength() {
    return length;
  }

  public BigInteger getP() {
    return p;
  }

  public BigInteger getG() {
    return g;
  }
}
